import {addTodos} from '../Apis/callApis';

const initialState = {
  todos : []
};

function rootReducer(state = initialState, action) {

  if(action.type === 'TODO_LIST') {
    console.log(state);
    state.todos = action.payload
  } else if(action.type === 'ADD_TODO_LIST') {
    let FD = new FormData();
    FD.append('todotext',action.payload.todo);
    FD.append('status',action.payload.checked);
    addTodos(JSON.stringify(FD)).then((res) => {
      console.log(res);
      state.todos.push(action.payload);
    }).catch((err) => {
      console.log(err);
    });

  }
  return state;
};

export default rootReducer;
